import React, { useState } from 'react';
import styled from 'styled-components';
import Table from './Table'

const Tab = styled.button`
  font-size: 20px;
  padding: 10px 60px;
  cursor: pointer;
  opacity: 0.6;
  background: white;
  border: 0;
  outline: 0;
  ${({ active }) =>
    active &&
    `
    border-bottom: 2px solid black;
    opacity: 1;
  `}
`;
const ButtonGroup = styled.p`
  display: flex;
`;
const types = ['Cash', 'Credit Card', 'Bitcoin', 'ACH'];

export const TabsGroup = () => {
    const [active, setActive] = useState(types[0]);
    return (
        <>
        <ButtonGroup>
            {types.map(type => (
            <Tab
                key={type}
                active={active === type}
                onClick={() => setActive(type)}
            >
                {type}
            </Tab>
            ))}
        </ButtonGroup>
        <p />
        <p> Your payment selection: {active} </p>
        <Table x={10} y={10} />
        </>
    );
};
